import React from 'react';
import Login from './components/login';
import Banner from './components/Banner'
import NewUserForm from './components/NewUserForm'
import { Route } from 'react-router-dom';
import {withRouter} from 'react-router';
import centerConsole from './containers/centerConsole'
import EditUserContainer from './containers/EditUserContainer'
import './App.css';
import UserCryptosContainer from './containers/UserCryptosContainer'
import UserCryptoDetailedView from './components/UserCryptoDetailedView'
import NewsContainer from './containers/newsContainer'
import ArticleView from './components/articleView'

const d = new Date();
function formatDate(date) {
    var d = new Date(date),
        month = '' + (d.getMonth() + 1),
        day = '' + d.getDate(),
        year = d.getFullYear();
    if (month.length < 2)
        month = '0' + month;
    if (day.length < 2)
        day = '0' + day;
    return [year, month, day].join('-');
}

const currentDate = formatDate(d)

const APIkey = "e17454af05b842518705a1a4960a4f94"

const USER_URL = "http://localhost:3000/api/v1/users"
const LOGIN_URL = "http://localhost:3000/api/v1/login"
const newsAPI = `https://newsapi.org/v2/everything?q=cryptocurrency&from=${currentDate}&sortBy=publishedAt&apiKey=${APIkey}`

class App extends React.Component{

  componentDidMount(){
    fetch(newsAPI)
      .then(res => res.json())
      .then(data => {
        this.setState({
          news: data.articles
        })
        console.log(data.articles)
      })
  }

  constructor(props){
    super(props)

    this.state = {
      current_user: {},
      error: "",
      feedback: "",
      userCryptos: [],
      hasClickedMyCryptos: false,
      currentCrypto: {},
      lookingAtSingleCrypto: false,
      cryptosAreLoading: false,
      news: [],
      hasClickedNewsDBButton: false,
      currentNewsArticle: {},
      lookingAtSingleNewsArticle: false,
      hasClickedSettings: false
    }

    this.createNewUser = this.createNewUser.bind(this)
    this.attemptLogin = this.attemptLogin.bind(this)
    this.setActiveUser = this.setActiveUser.bind(this)
    this.logout = this.logout.bind(this)
    this.updateUser = this.updateUser.bind(this)
    this.deleteUser = this.deleteUser.bind(this)
    this.toggleMyCryptos = this.toggleMyCryptos.bind(this)
    this.setError = this.setError.bind(this)
    this.setFeedback = this.setFeedback.bind(this)


    this.renewState();
  }

  // news functions

  setNewsState = () => {
    this.setState({
      hasClickedNewsDBButton: true
    })
    console.log("hello")
  }

///////////////////////////////

  setFeedback(str){
    this.setState({
      feedback: str
    })
    setTimeout(() => this.setState({feedback: ""}), 1500)
  }

  setError(str){
    this.setState({
      error: str
    })
    setTimeout(() => this.setState({error: ""}), 1500)
  }

  createNewUser(user){
    fetch(USER_URL, {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify({user})
    })
    .then(res => res.json())
    .then(data => {
      this.setActiveUser(data)
      this.props.history.push('/centerConsole')
    })
  }

  attemptLogin(user){
    fetch(LOGIN_URL, {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify({user})
    })
    .then(res => res.json())
    .then(data => {
      this.setActiveUser(data)
      if(!data.message){
        this.props.history.push('/centerConsole')
      }
    })
  }

  setActiveUser(data, mode="hard"){
    if(data.message && mode === "soft"){
      return
    } else if(data.message){
      this.setState({error: data.message})
    } else {
      this.setState({
        current_user: data.user,
        error: ""
      })
      if(data.jwt){localStorage.token = data.jwt}
    }
  }

  logout(){
    this.setState({
      current_user: {},
      hasClickedMyCryptos: false,
      currentCrypto: {},
      lookingAtSingleCrypto: false
    })
    delete localStorage.token
    this.props.history.push('/login')
  }

  renewState(){
  if(!localStorage.token){return}
  fetch("http://localhost:3000/api/v1/profile", {
    method: "GET",
    headers: {
      'Authorization': "Bearer " + localStorage.token
    }
  })
  .then(res => res.json())
  .then(data => this.setActiveUser(data, "soft"))
}

  displayUserCryptos = () => {
    if(!localStorage.token){return}
    fetch("http://localhost:3000/api/v1/profile", {
      method: "GET",
      headers: {
        'Authorization': "Bearer " + localStorage.token,
      }
    })
    .then(res => res.json())
    .then(data => {
      this.setState({
        userCryptos: data.user.cryptos,
        hasClickedMyCryptos: !this.state.hasClickedMyCryptos,
        cryptosAreLoading: false
      })
      console.log(data)
    })
  }


  toggleMyCryptos() {
    this.setState({
      hasClickedMyCryptos: !this.state.hasClickedMyCryptos
    })
  }

  updateUser(user){
    fetch(USER_URL + `/${user.id}`, {
      method: "PATCH",
      headers: {
        'Content-Type': 'application/json',
        'Authorization': "Bearer " + localStorage.token,
        'Accept': 'application/json'
      },
      body: JSON.stringify({
        user
      })
    })
    .then(res => res.json())
    .then(data => {
      this.setActiveUser(data)
      this.props.history.push('/centerConsole')
    })
  }

  deleteUser(id){
    fetch(USER_URL + `/${id}`, {
      method: "DELETE",
      headers: {
        'Authorization': "Bearer " + localStorage.token
      }
    })
    .then( () => this.logout())
  }

  setCurrentCrypto = (crypto) => {
    this.setState({
      currentCrypto: crypto,
      lookingAtSingleCrypto: true
    })
    this.props.history.push('/my-crypto/' + crypto.id)

  }

  setCurrentCrypto1 = (crypto) => {
    this.setState({
      currentCrypto: crypto,
      lookingAtSingleCrypto: true
    })
    // this.props.history.push('/my-crypto/' + crypto.id)

  }

  renderUserCryptos = () => {
    const {userCryptos} = this.state
    if(this.state.hasClickedMyCryptos === true &&!this.state.cryptosAreLoading) {
      return <UserCryptosContainer
              returnToHomepageFromCryptosContainers={this.returnToHomepageFromCryptosContainers}
              toggleCryptos={this.toggleMyCryptos}
              userCryptos={userCryptos}
              setCurrentCrypto={this.setCurrentCrypto}
              />
    }
  }

  countCrypto = (cc) => {
    const cryptos = []
    this.state.userCryptos.map((crypto) => {
      if(cc.name === crypto.name) {
        cryptos.push(crypto)
        console.log(crypto)
      }
    })
    return cryptos.length
  }

  renderNews = () => { //this may not actually be necessary. I guess I wrote this back when I didnt fully understand
                        // how this code worked yet
    const {news} = this.state
    if(this.state.hasClickedNewsDBButton === true){
      return <NewsContainer
              returnToHomepageFromNewsContainer={this.returnToHomepageFromNewsContainer}
              toggleNews={this.toggleNews}
              setCurrentNewsArticle={this.setCurrentNewsArticle}
              news={news}
              currentNewsArticle={this.state.currentNewsArticle}
              lookingAtSingleNewsArticle={this.state.lookingAtSingleNewsArticle}
              />
    }
  }


  renderDetailedUserCryptoView = () => {
    const {userCryptos} = this.state
    const {currentCrypto} = this.state
    if(this.state.lookingAtSingleCrypto === true) {
      return(
        <div>
              <UserCryptosContainer
              returnToHomepageFromCryptosContainers={this.returnToHomepageFromCryptosContainers}
              toggleCryptos={this.toggleMyCryptos}
              userCryptos={userCryptos}
              setCurrentCrypto={this.setCurrentCrypto}
              />
              <UserCryptoDetailedView
              countCrypto={this.countCrypto}
              currentCrypto={currentCrypto}
              returnToUserCryptosContainer={this.returnToUserCryptosContainer}
              deleteCrypto={this.deleteCrypto}
              error={this.state.error}
              />
        </div>
          )
    }

  }

  returnToUserCryptosContainer = () => {
    this.setState({
      currentCrypto: null,
      lookingAtSingleCrypto: false,
      hasClickedMyCryptos: true
    })
    this.props.history.push('/centerConsole')
  }

  returnMainMenu = () => {
    this.setState({
      hasClickedMyCryptos: false,
      lookingAtSingleCrypto: false
    })
    this.props.history.push('/centerConsole')
  }

  returnToHomepageFromCryptosContainers = () => {
    console.log("hello")
    this.setState({
      cryptosAreLoading: true,
      currentCrypto: null,
      lookingAtSingleCrypto: false,
      hasClickedMyCryptos: true
    })
  }


  deleteCrypto = (crypto) => {
    const cryptoId = crypto.id
    fetch("http://localhost:3000/api/v1/remove_crypto/"+ cryptoId, {
      method: "DELETE",
      headers: {
        'Authorization': "Bearer " + localStorage.token
      },
      body: JSON.stringify({crypto_id: cryptoId})
    })
    .then(res => res.json())
    .then(data => {
      let cryptoNames = data.user.cryptos.map((crypto) => {
        return crypto.name
      })
      // console.log(cryptoNames)
      // console.log(data.user.cryptos)
      if(!cryptoNames.includes(crypto.name)){
        setTimeout(() => this.setState({
          currentCrypto: null,
          lookingAtSingleCrypto: false,
        }), 2000)
      }


      this.setError(`Deleted ${crypto.name} from Cryptos`)
      this.displayUserCryptos()
    })

  }




renderEditUser = () => {
