import React from 'react'
import UserCrypto from '../components/UserCrypto'

class UserCryptoContainer extends React.Component {

getPrices = () => {
  const values = []
  this.props.userCryptos.map((crypto)=> {
    values.push(parseInt(crypto.price))
  })
  if(values.length >= 1) {
  let total = values.reduce((x, y) => x + y)
  return total
}
  let total  = 0
  return total
}

countCrypto = (cc) => {
  const cryptos = []
  this.props.userCryptos.map((crypto) => {
    if(cc.name === crypto.name) {
      cryptos.push(crypto)
      console.log(crypto)
    }
  })
  return cryptos.length
}

distinctCrypto = () => {
  const array = [
    { id: 3, name: 'Central Microscopy', fiscalYear: 2018 },
    { id: 5, name: 'Crystallography Facility', fiscalYear: 2018 },
    { id: 3, name: 'Central Microscopy', fiscalYear: 2017 },
    { id: 5, name: 'Crystallography Facility', fiscalYear: 2017 }
  ];
const result = [];
const map = new Map();
for (const item of array) {
    if(!map.has(item.id)){
        map.set(item.id, true);    // set any value to Map
        result.push({
            id: item.id,
            name: item.name
        });
    }
}
console.log(result)
}

  render(){
    return(
      <div className="userCryptos">
      <h1>My Cryptocurrencies</h1>
      <h2>Total portfolio value: ${this.getPrices()}</h2>
      <button className="CCbutton" onClick={this.props.returnHome}>Return to homepage</button>

      <div>
      {this.distinctCrypto()}
      {this.props.userCryptos.map((crypto)=>{
        return <UserCrypto
                countCrypto={this.countCrypto}
                key={"user-"+crypto.name+"-"+crypto.id}
                toggleCryptos={this.props.toggleCryptos}
                crypto={crypto}
                setCurrentCrypto={this.props.setCurrentCrypto}
                />
      })}
      </div>
      </div>
    )
  }

}

export default UserCryptoContainer
