import React from 'react'

class cryptoNameForm extends React.Component {

  constructor(){
    super()
    this.state = {
      searchTerm: "",
      cryptos: [],
      lookingAtSingleCrypto: false,
      cryptoContainerIsOpen: false,
      currentCrypto: {},
      feedback: ""
    }
  }

  componentDidMount(){
    fetch("http://localhost:3000/api/v1/searchbyname")
      .then(res => res.json())
      .then(data => {
        this.setState({
          cryptos: data
          })
      })
  }

  turnOn = () => {
    this.setState({
      cryptoContainerIsOpen: true
    })
    console.log("hello")
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  handleSubmit = (event) => {
    event.preventDefault()
    this.props.getCryptosName()
    this.setState({
      searchTerm: ""
    })
  }

  renderCryptos() {
    if(this.state.cryptoContainerIsOpen === true){
      return  <div className="cryptosContainer">
        <h2 className="card-title">View Cryptos</h2>
        <CryptosContainer
          cryptos={this.state.cryptos}
          setCurrentCrypto={this.setCurrentCrypto}
          returnToHomepageFromCryptosContainers={this.returnToHomepageFromCryptosContainers}
          />
      </div>
    }
  }

  render(){
    return(
      <div>
            {this.renderCryptos()}
            <button className="crypto-button" onClick={this.turnOn}>
              See all cryptocurrencies

            </button>
      </div>
    )
  }

}

export default cryptoNameForm
