import React, { Component } from 'react'
import Article from '../components/article'
// import {NEWS_API} from '../constants.js'

const NEWS_API= "https://newsapi.org/v2/everything?q=cryptocurrency&from=2019-12-10&sortBy=publishedAt&apiKey=e17454af05b842518705a1a4960a4f94"


class NewsContainer extends Component {

  constructor(){
    super()
    this.state = {
      news: []
    }
  }

  // componentDidMount(){
  //   fetch(NEWS_API)
  //     .then(res => res.json())
  //     .then(data => {
  //       this.setState({
  //         news: data.articles
  //       })
  //     })
  //     console.log(this.state)
  // }

  getNewsNow(){
    fetch(NEWS_API)
      .then(res => res.json())
      .then(data => {
        this.setState({
          news: data.articles
        })
      })
      console.log(this.state)
  }

  render(){

    return (

      <div className="articles">
      <h1 className="ccHeader">News Stories</h1>
      <button className="CCbutton" onClick={this.props.returnToHomepageFromNewsContainer}>Return to homepage</button>

      <div>
        {
          this.props.news.map((article)=> {

            return <Article
                    className="article"
                    article={article}
                    setCurrentNewsArticle={this.props.setCurrentNewsArticle}
                    />
          })
        }
      </div>
      </div>

    )
  }
}

export default NewsContainer
