import React, { Component } from 'react'
import CryptosContainer from './cryptosContainer'
import CryptoNameForm from '../components/cryptoNameForm'
import {Redirect} from 'react-router-dom'
import CryptoDetailedView from '../components/CryptoDetailedView'

// const nameURL = "https://min-api.cryptocompare.com/data/top/totalvolfull?limit=10&tsym=USD&api_key={2eb0a0afcdbd0af89e90104132e9424984ac9324e5c2b62272a6afbe9567cb19}"
const URL = "http://localhost:3000/api/v1/searchbyname"
// const ingredientURL = "http://localhost:3000/api/v1/searchbyingredient?searchTerm="

export default class centerConsole extends Component {

  constructor(props){
    super(props)
      this.state = {
        cryptos: [],
        lookingAtSingleCrypto: false,
        currentCrypto: {},
        cryptosAreLoading: false
      }
    }

  getCryptosName = () => {
    this.setLoading()
    fetch(URL)
      .then(res => res.json())
      .then(data => {
        this.setState({
          cryptos: data.Data,
          cryptosAreLoading: false
        })
        console.log(data)
      })
  }

  // componentDidMount(){
  //   fetch(URL)
  //   .then(res => res.json())
  //   .then(data => {
  //     console.log(data)
  //   })
  // }



  // getCryptosIngredient = (event) => {
  //   this.setLoading()
  //   let searchedCrypto = event.target.elements['searchTerm'].value
  //   fetch(ingredientURL + searchedCrypto)
  //     .then(res => res.json())
  //     .then(data => {
  //       if(data.length === 0){
  //         this.props.setFeedback("No cryptos found matching the given criteria")
  //       }
  //       this.setState({
  //         cryptos: data,
  //         cryptosAreLoading: false
  //       })
  //     })
  // }

  setCurrentCrypto = (crypto) => {
    this.setState({
      currentCrypto: crypto,
      lookingAtSingleCrypto: true
    })

  }

  setLoading(){
    this.setState({CryptosAreLoading: true})
  }

  returnMainMenu = () => {

    this.setState({
      currentCrypto: null,
      lookingAtSingleCrypto: false
    })
  }


  addCrypto = (crypto) => {
    fetch("http://localhost:3000/api/v1/add_crypto", {
      method: 'POST',
      body: JSON.stringify({crypto_id: crypto.id}),
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + localStorage.token
      }
    })
    .then(res => res.json())
    .then(data => {
      this.props.setFeedback(`Added ${crypto.CoinInfo.Name} to "My Cryptos"`)
    })
    console.log(crypto)
  }




  renderCryptos() {
    if(this.state.cryptos.length === 0 && !this.state.cryptosAreLoading){
      return null
    } else if (!this.state.cryptosAreLoading){
      return  <div className="cryptosContainer">
        <h2 className="card-title">Found Cryptos</h2>
        <CryptosContainer cryptos={this.state.cryptos}
                                    setCurrentCrypto={this.setCurrentCrypto}/>
      </div>

    } else {
        return <div className="cryptosContainer">
          <div className="loading-dimmer ui segment">
            <div className="ui active dimmer">
              <div className="ui text loader">Finding Cryptos...</div>
            </div>
          </div>
        </div>
    }


  }


  renderDetailedUserCryptoView = () => {
    const {currentCrypto} = this.state
    if(this.state.lookingAtSingleCrypto === true) {
      return <CryptoDetailedView
              currentCrypto={currentCrypto}
              returnMainMenu={this.returnMainMenu}
              addCrypto={this.addCrypto}
              />
    } else {
      return <div className="App">
            {this.renderCryptos()}
          <h1 className="title">My Crypto</h1>
          <div className="forms">
            <CryptoNameForm getCryptosName={this.getCryptosName}/>

          <br/>
          </div>
        </div>
    }
  }

  render(){
    if(!localStorage.token){
      return <Redirect to="/login" />
    }

    return (
      <div>

        {this.renderDetailedUserCryptoView()}
      </div>
    );
  }
}
