import React from 'react'

class CryptoDetailedView extends React.Component {

  render(){
    return(
      <div className="CryptoDetailedView">
          <h1 className="card-title">{this.props.currentCrypto.CoinInfo.Name}</h1>
          <h3>{this.props.currentCrypto.DISPLAY.USD.PRICE}</h3>
          {console.log(this.props.currentCrypto)}



          <button className="ui left inverted green labeled icon button" onClick={this.props.returnMainMenu}>
            <i className="arrow left icon"></i>
            Go back to cryptos
          </button>
          <button className="ui inverted right green labeled icon button" onClick={()=> this.props.addCrypto(this.props.currentCrypto)}>
            Save crypto
            <i className="right glass martini icon"></i>
          </button>

      </div>
    )
  }

}

export default CryptoDetailedView
